mapper.select:
select menu_id, menu_parent_id, menu_type, menu_name, menu_icon, menu_key, menu_component, show_order, disabled, update_time, EXISTS (select 1 from s_menu where del_status =0 and menu_parent_id = s.menu_id) as hasChildren from s_menu s where del_status =0 and menu_parent_id = #{menuParentId,jdbcType=BIGINT}
and menu_name like concat('%', #{menuName,jdbcType=VARCHAR}, '%')
and menu_type = #{menuType,jdbcType=TINYINT}
and disabled = #{disabled,jdbcType=TINYINT}
order by show_order desc, update_time desc

mapper.update:
update s_menu
set menu_parent_path = REPLACE(menu_parent_path, #{oldPath}, #{newPath})
where menu_parent_path rlike #{oldPath}

mapper.select:
select
exists (
select 1 from s_menu
where del_status = 0
and menu_parent_id = #{menuParentId}
and (menu_name = #{menuName} or menu_key = #{menuKey})

  and menu_id != #{menuId}

)
